// Obtener el timestamp actual
const ahora = new Date();

// Obtener el timestamp de creación del registro de Airtable
const creadoEn = new Date($input.item.json.fields.Created);

// Calcular la diferencia en minutos
const diferenciaMinutos = (ahora - creadoEn) / (1000 * 60);

// Si el registro fue creado hace menos de 2 minutos, procesarlo
if (diferenciaMinutos <= 2) {
  return {
    json: {
      esNuevo: true,
      registro: $input.item.json,
      minutosDesdeSuCreacion: diferenciaMinutos
    }
  };
} else {
  return {
    json: {
      esNuevo: false,
      minutosDesdeSuCreacion: diferenciaMinutos
    }
  };
}
