// JavaScript code for n8n Code node to prepare HTML email for Gmail API
const emailData = {
  messageId: $input.item.json['Message ID'],
  threadId: $input.item.json['Thread ID'] || $input.item.json['Message ID'],
  subject: $input.item.json['Email#1 Subject'],
  senderEmail: $input.item.json['Sender Email'],
  recipientEmail: $input.item.json['Email Address'],
  token: $input.item.json['Token']
};

// Check available input keys to help diagnose the issue
const inputKeys = Object.keys($input.item.json);
console.log('Available input keys:', inputKeys);

// Try to find the email body in different possible formats
let emailBodyContent = '';
// First try the exact key name
if ($input.item.json['Email#2 Body'] !== undefined) {
  emailBodyContent = $input.item.json['Email#2 Body'];
} 
// Try with a space after the hash
else if ($input.item.json['Email #2 Body'] !== undefined) {
  emailBodyContent = $input.item.json['Email #2 Body'];
} 
// Try other possible variations
else if ($input.item.json['EmailBody'] !== undefined) {
  emailBodyContent = $input.item.json['EmailBody'];
} 
// Default fallback message if none found
else {
  emailBodyContent = "Thank you for your attention to this matter.";
  console.log("No email body found, using default text");
}

// Ensure message ID is properly formatted
const formattedMessageId = emailData.messageId.startsWith('<') ? 
  emailData.messageId : 
  `<${emailData.messageId}>`;

// Create HTML email body with unsubscribe link
const htmlBody = `${emailBodyContent}<br><br>
<a href="https://kiaghasem.app.n8n.cloud/webhook/unsubscribe?tkn=${emailData.token}">Click Here to Unsubscribe</a>`;

// Create email in RFC 2822 format with HTML content type
const emailContent = 
  `In-Reply-To: ${formattedMessageId}\n` +
  `References: ${formattedMessageId}\n` +
  `Subject: Re: ${emailData.subject}\n` +
  `From: ${emailData.senderEmail}\n` +
  `To: ${emailData.recipientEmail}\n` +
  `Content-Type: text/html; charset="UTF-8"\n\n` +
  `${htmlBody}`;

// Base64 encode and convert to base64url format
const base64EncodedEmail = Buffer.from(emailContent)
  .toString('base64')
  .replace(/\+/g, '-')
  .replace(/\//g, '_')
  .replace(/=+$/, '');

// Return an object with the encoded email and thread ID
return {
  json: {
    raw: base64EncodedEmail,
    threadId: emailData.threadId
  }
};

