// Código para nodo "Code" en n8n
// Resta 2 horas a la variable Fecha_cita

// Obtener la fecha original
const fechaOriginal = $input.first().json.body.args.Fecha_cita;

// Crear objeto Date y restar 2 horas
const fecha = new Date(fechaOriginal);
const fechaModificada = new Date(fecha.getTime() - 2 * 60 * 60 * 1000);

// Convertir de vuelta a formato ISO
const fechaFinal = fechaModificada.toISOString();

// Retornar el resultado
return [
  {
    json: {
      Fecha_cita: fechaFinal,
      fecha_original: fechaOriginal
    }
  }
];

