// Fecha solicitada por el usuario
const fechaSolicitada = $('Edit Fields').first().json.fecha_solicitada;

// Contenedor de fechas de Airtable (objeto con keys indexadas)
const contenedor = $input.first().json['Fecha de la Cita'];

// Recolectar todas las fechas en un array plano
let fechas = [];
if (Array.isArray(contenedor)) {
  fechas = contenedor.filter(Boolean);
} else if (contenedor && typeof contenedor === 'object') {
  for (const k of Object.keys(contenedor)) {
    const v = contenedor[k];
    if (v) fechas.push(v);
  }
}

// Normalizar a timestamp (milisegundos UTC)
const toTs = v => new Date(v).getTime();
const tsSolicitada = toTs(fechaSolicitada);

// Buscar coincidencia exacta
const coincidencia = fechas.some(f => toTs(f) === tsSolicitada);

// Preparar salida
return [{
  json: {
    status: coincidencia ? "NO DISPONIBLE" : "DISPONIBLE",
    fecha_solicitada: fechaSolicitada,
    coincidencia_encontrada: coincidencia,
    total_fechas_revisadas: fechas.length,
    fechas_revisadas: fechas // quita esto si no lo necesitas
  }
}];

// Código para nodo "Code" en n8n
// Compara el teléfono del cliente con el array de teléfonos en Airtable

// Obtener el teléfono del cliente que solicita la cita
const telefonoCliente = $('Edit Fields').first().json.telefono_cliente;

// Obtener el array de teléfonos desde Airtable
const telefonosAirtable = $input.first().json['Teléfono'];

// Función para normalizar teléfonos (eliminar espacios, guiones, etc.)
function normalizarTelefono(telefono) {
  if (!telefono) return '';
  return telefono.toString().replace(/[\s\-\(\)\+]/g, '');
}

// Normalizar el teléfono del cliente
const telefonoClienteNormalizado = normalizarTelefono(telefonoCliente);

// Verificar si el teléfono coincide con alguno en el array de Airtable
let coincidenciaEncontrada = false;

if (Array.isArray(telefonosAirtable)) {
  for (const telefonoAirtable of telefonosAirtable) {
    if (telefonoAirtable) {
      const telefonoAirtableNormalizado = normalizarTelefono(telefonoAirtable);

      // Comparar teléfonos normalizados
      if (telefonoAirtableNormalizado === telefonoClienteNormalizado && telefonoClienteNormalizado !== '') {
        coincidenciaEncontrada = true;
        break;
      }
    }
  }
}

// Determinar el resultado
const resultado = coincidenciaEncontrada ? "CLIENTE EXISTENTE" : "CLIENTE NUEVO";

// Retornar resultado
return [
  {
    json: {
      status: resultado,
      telefono_cliente: telefonoCliente,
      telefono_normalizado: telefonoClienteNormalizado,
      total_telefonos_en_airtable: Array.isArray(telefonosAirtable) ? telefonosAirtable.length : 0,
      telefonos_airtable: telefonosAirtable,
      coincidencia_encontrada: coincidenciaEncontrada
    }
  }
];

